{"changed":true,"filter":false,"title":"shared_books_controller.rb","tooltip":"/app/controllers/shared_books_controller.rb","value":"class SharedBooksController < ApplicationController\n  before_action :set_shared_book, only: [:show, :edit, :update, :destroy]\n\n  # GET /shared_books\n  # GET /shared_books.json\n  def index\n    if params[:user_id]\n      @shared_books = SharedBook.where(user_id: params[:user_id])\n    elsif params[:book_id]\n      @shared_books = SharedBook.where(book_id: params[:book_id])\n    else\n      @shared_books = SharedBook.all\n    end\n  end\n\n  # GET /shared_books/1\n  # GET /shared_books/1.json\n  def show\n  end\n\n  # GET /shared_books/new\n  def new\n    @shared_book = SharedBook.new(:book_id => params[:book_id])\n  end\n\n  # GET /shared_books/1/edit\n  def edit\n  end\n\n  # POST /shared_books\n  # POST /shared_books.json\n  def create\n    @shared_book = SharedBook.new(shared_book_params)\n    @shared_book.user_id = current_user.id\n\n    respond_to do |format|\n      if @shared_book.save\n        format.html { redirect_to user_shared_books_path(current_user.id), notice: 'Shared book was successfully created.' }\n        format.json { render :show, status: :created, location: @shared_book }\n      else\n        format.html { render :new }\n        format.json { render json: @shared_book.errors, status: :unprocessable_entity }\n      end\n    end\n  end\n\n  # PATCH/PUT /shared_books/1\n  # PATCH/PUT /shared_books/1.json\n  def update\n    respond_to do |format|\n      if @shared_book.update(shared_book_params)\n        format.html { redirect_to user_shared_books_path(current_user.id), notice: 'Shared book was successfully updated.' }\n        format.json { render :show, status: :ok, location: @shared_book }\n      else\n        format.html { render :edit }\n        format.json { render json: @shared_book.errors, status: :unprocessable_entity }\n      end\n    end\n  end\n\n  # DELETE /shared_books/1\n  # DELETE /shared_books/1.json\n  def destroy\n    @shared_book.destroy\n    respond_to do |format|\n      format.html { redirect_to user_shared_books_path(current_user.id), notice: 'Shared book was successfully destroyed.' }\n      format.json { head :no_content }\n    end\n  end\n\n  private\n    # Use callbacks to share common setup or constraints between actions.\n    def set_shared_book\n      @shared_book = SharedBook.find(params[:id])\n    end\n\n    # Never trust parameters from the scary internet, only allow the white list through.\n    def shared_book_params\n      params.require(:shared_book).permit(:user_id, :book_id, :credit_price)\n    end\nend\n","undoManager":{"mark":59,"position":100,"stack":[[{"group":"doc","deltas":[{"start":{"row":33,"column":16},"end":{"row":33,"column":17},"action":"insert","lines":["."]}]}],[{"group":"doc","deltas":[{"start":{"row":33,"column":17},"end":{"row":33,"column":18},"action":"insert","lines":["b"]}]}],[{"group":"doc","deltas":[{"start":{"row":33,"column":18},"end":{"row":33,"column":19},"action":"insert","lines":["o"]}]}],[{"group":"doc","deltas":[{"start":{"row":33,"column":19},"end":{"row":33,"column":20},"action":"insert","lines":["o"]}]}],[{"group":"doc","deltas":[{"start":{"row":33,"column":17},"end":{"row":33,"column":20},"action":"remove","lines":["boo"]},{"start":{"row":33,"column":17},"end":{"row":33,"column":24},"action":"insert","lines":["book_id"]}]}],[{"group":"doc","deltas":[{"start":{"row":33,"column":24},"end":{"row":33,"column":25},"action":"insert","lines":[" "]}]}],[{"group":"doc","deltas":[{"start":{"row":33,"column":25},"end":{"row":33,"column":26},"action":"insert","lines":["="]}]}],[{"group":"doc","deltas":[{"start":{"row":33,"column":26},"end":{"row":33,"column":27},"action":"insert","lines":[" "]}]}],[{"group":"doc","deltas":[{"start":{"row":33,"column":27},"end":{"row":33,"column":28},"action":"insert","lines":["p"]}]}],[{"group":"doc","deltas":[{"start":{"row":33,"column":28},"end":{"row":33,"column":29},"action":"in